
security {

	srp {
		active = true
        srpAsDefaultLoginFormUrl = true
		sessionKey = 'SPRING_SECURITY_SRP_SESSION'
        step1filterProcessesUrl = '/srpLogin/challenge'
        step1UsernameParameter = 'j_username'
        step1SaltParameter = 'j_s'
        step1BParameter = 'j_b'
        step2filterProcessesUrl = '/login/validate'
		step2AParameter = 'j_a'
        step2M1Parameter = 'j_m1'
        step2postOnly = true
        step3M2Parameter = 'j_m2'

        userLookup {
            saltPropertyName = 'salt'
            verifierPropertyName = 'verifier'
        }

        gsp {
            layoutAuth = 'main'
            layoutDenied = 'main'
        }

        cryptoParams {
			N_base10 = ""
		    g_base10 = "2"
		    k_base16 = "1f108f31bb70bd944efa49b1821ed93218a59dfa17a7bc37b29cdd1645e2ea9f"
		    hash = "SHA-256"
		}
	}
}